'use strict';

const got = require('got');
const uniqueRandomArray = require('unique-random-array');
const EventEmitter = require('eventemitter3');

const randomCache = {};

function formatRedditResult(getRandomImage) {
    const imageData = getRandomImage();
    if (!imageData) {
        return;
    }
    return `i.redd.it/${imageData.hash}${imageData.ext.replace(/\?.*/, '')}`;
}

function formatImgurResult(getRandomImage) {
    const imageData = getRandomImage();
    if (!imageData) {
        return;
    }
    return `i.imgur.com/${imageData.hash}${imageData.ext.replace(/\?.*/, '')}`;
}

function storeResults(images, subreddit) {
    const getRandomImage = uniqueRandomArray(images);

    randomCache[subreddit] = getRandomImage;
    return getRandomImage;
}

function randomPuppy(subreddit, site) {
    subreddit = (typeof subreddit === 'string' && subreddit.length !== 0) ? subreddit : 'puppies';

    if (randomCache[subreddit]) {
        if(site === "Reddit")
            return Promise.resolve(formatRedditResult(randomCache[subreddit]));
        if(site === "Imgur")
            return Promise.resolve(formatImgurResult(randomCache[subreddit]));
    }

    if(site === "Reddit")
    {
        return got(`https://reddit.com/r/${subreddit}.json`, {json: true})
            .then(response => storeResults(response.body.data, subreddit))
            .then(getRandomImage => formatRedditResult(getRandomImage));
    }

    if(site === "Imgur")
    {
        return got(`https://reddit.com/r/${subreddit}.json`, {json: true})
            .then(response => storeResults(response.body.data, subreddit))
            .then(getRandomImage => formatImgurResult(getRandomImage));
    }
}

// silly feature to play with observables
function all(subreddit) {
    const eventEmitter = new EventEmitter();

    function emitRandomImage(subreddit) {
        randomPuppy(subreddit, site).then(imageUrl => {
            eventEmitter.emit('data', imageUrl + '#' + subreddit);
            if (eventEmitter.listeners('data').length) {
                setTimeout(() => emitRandomImage(subreddit), 200);
            }
        });
    }

    emitRandomImage(subreddit);
    return eventEmitter;
}
/*
function callback(subreddit, cb) {
    randomPuppy(subreddit)
        .then(url => cb(null, url))
        .catch(err => cb(err));
}
*/
// subreddit is optional
// callback support is provided for a training exercise
module.exports = (subreddit, site) => {
    return randomPuppy(subreddit, site);
};

module.exports.all = all;
